    debug { warn "#{ "  " * @level}EVAL    lf #{chunk.pos} #{@buffer[chunk.pos..-1].inspect}" }
    @level += 1
    chunk.resize_captures(0)
    catch(0) do
      1.times do |;pos, p_loc, n|
        pos = @cur
        p_loc = @cur_loc
        n = chunk.thunks.length
        catch(2) do
          catch(1) do
            if (
              refill_buffer(1) < 1 ||
              @buffer[@cur] != "\r"
            )
              throw(1)
            end
            @cur_loc = @cur_loc.forward(@buffer, @cur, 1)
            @cur += 1
            throw(2)
          end
          @cur_loc = p_loc
          @cur = pos
          chunk.thunks[n..-1] = []
        end
      end
      if (
        refill_buffer(1) < 1 ||
        @buffer[@cur] != "\n"
      )
        throw(0)
      end
      @cur_loc = @cur_loc.forward(@buffer, @cur, 1)
      @cur += 1
      debug { warn "#{ "  " * @level}MATCH   lf #{chunk.pos} #{@buffer[chunk.pos...@cur].inspect}" }
      return chunk
    ensure
      @level -= 1
    end
    debug { warn "#{ "  " * @level}NOMATCH lf #{chunk.pos} #{@buffer[chunk.pos...@cur].inspect}" }
    return nil
  end

  def evaluate_rule_footer
    chunk = ThunkChunk.new
    chunk.pos = @cur
    chunk.pos_loc = @cur_loc
    debug { warn "#{ "  " * @level}EVAL    footer #{chunk.pos} #{@buffer[chunk.pos..-1].inspect}" }
    @level += 1
    chunk.resize_captures(1)
    catch(0) do
      if (
        refill_buffer(2) < 2 ||
        @buffer[@cur, 2] != "%%"
      )
        throw(0)
      end
      @cur_loc = @cur_loc.forward(@buffer, @cur, 2)
      @cur += 2
      if !apply_rule(:evaluate_rule_lf, chunk.thunks, nil, 0)
        throw(0)
      end
      1.times do |;pos, q, capt, p_loc, q_loc|
        pos = @cur
        p_loc = @cur_loc
        1.times do |;p0, p0_loc, n0, i, pos, p_loc, n, stat|
          i = 0
          catch(1) do
            pos = @cur
            p_loc = @cur_loc
            n = chunk.thunks.length
            1.times do |;u, n|
              if refill_buffer(1) < 1
                throw(1)
              end
              u = @buffer[@cur]
              @cur_loc = @cur_loc.forward(@buffer, @cur, 1)
              @cur += 1
            end
            i += 1
            if @cur != pos
              redo
            end
            pos = nil
          end
          if pos
            @cur = pos
            @cur_loc = p_loc
            chunk.thunks[n..-1] = []
          end
        end
        q = @cur
        capt = chunk.capts[0]
        capt.range_start = pos
        capt.range_end = q
        q_loc = @cur_loc
        capt.start_loc = p_loc
        capt.end_loc = q_loc
      end
      chunk.thunks.push(
        ThunkLeaf.new(
          :action_footer_0,
          Capture.new(
            chunk.pos, @cur,
            chunk.pos_loc, @cur_loc,
          ),
          {},
          chunk.capts.slice(0),
        )
      )
      debug { warn "#{ "  " * @level}MATCH   footer #{chunk.pos} #{@buffer[chunk.pos...@cur].inspect}" }
      return chunk
    ensure
      @level -= 1
    end
    debug { warn "#{ "  " * @level}NOMATCH footer #{chunk.pos} #{@buffer[chunk.pos...@cur].inspect}" }
    return nil
  end


  def rule_chunk(rule, thunks, values, index)
    pos = @pos + @cur
    p_loc = @pos_loc + @cur_loc
    answer = @lrtable.answers[pos, rule]
    head = @lrtable.heads[pos]

    if head
      if !answer && rule != head.rule_name && !head.invol[rule]
        return nil
      end
      if head.eval.delete(rule)
        return public_send(rule)
      end
    end

    if answer
      @cur = answer.pos - @pos
      @cur_loc = answer.pos_loc - @pos_loc
      if !answer.lr
        return answer.chunk
      end
      lr = answer.lr
      head = lr.head
      if !head
        head = LrHead.new
        lr.head = head
        head.rule_name = rule
      end
      @lrstack.reverse_each do |lrentry|
        entry_head = lrentry.head
        lr = answer.lr
        answer_head = lr.head
        if entry_head == answer_head
          break
        end
        lrentry.head = answer_head
        invol = answer_head.invol
        invol[lrentry.rule] = true
      end
      return answer.lr.seed
    end

    entry = LrEntry.new
    entry.rule = rule
    @lrstack.push(entry)
    answer = LrAnswer.new(entry, pos, p_loc)
    @lrtable.answers[pos, rule] = answer
    chunk = public_send(rule)
    @lrstack.pop
    answer.pos = @pos + @cur
    answer.pos_loc = @pos_loc + @cur_loc
    if !entry.head
      answer.chunk = chunk
      return chunk
    end

    lr = answer.lr
    entry.seed = chunk
    head = lr.head
    if head.rule_name != rule
      return lr.seed
    end

    seed = lr.seed
    answer.chunk = seed
    chunk = answer.chunk
    if !chunk
      return nil
    end
    @lrtable.heads[pos] = head
    while true
      @cur = pos - @pos
      @cur_loc = p_loc - @pos_loc
      head.invol_to_eval
      chunk = public_send(rule)
      if !chunk || @pos + @cur <= answer.pos
        break
      end
      answer.chunk = chunk
      answer.pos = @pos + @cur
      answer.pos_loc = @pos_loc + @cur_loc
    end
    @lrtable.heads[pos] = nil
    @cur = answer.pos - @pos
    @cur_loc = answer.pos_loc - @pos_loc
    answer.chunk
  end

  def apply_rule(rule, thunks, values, index)
    chunk = rule_chunk(rule, thunks, values, index)
    if !chunk
      return false
    end
    values ||= @global_values
    thunk = ThunkNode.new(chunk.thunks, values, index)
    thunks << thunk
    return true
  end

  def do_action(thunks, values, index)
    thunks.each do |thunk|
      thunk.do_action(self, values, index)
    end
  end

  class Location
    attr_reader :charnum, :linenum

    def initialize(charnum = 0, linenum = 0)
      @charnum = charnum
      @linenum = linenum
    end

    def +(other)
      if other.linenum == 0
        Location.new(@charnum + other.charnum, @linenum + other.linenum)
      else
        Location.new(           other.charnum, @linenum + other.linenum)
      end
    end

    def -(other)
      if other.linenum == self.linenum
        Location.new(@charnum - other.charnum, @linenum - other.linenum)
      elsif other.charnum == 0
        Location.new(@charnum - other.charnum, @linenum - other.linenum)
      else
        raise "unexpected location #{self.inspect} - #{other.inspect}"
      end
    end

    def forward(buffer, cur, n)
      Location.new(@charnum, @linenum).forward!(buffer, cur, n)
    end

    def forward!(buffer, cur, n)
      buffer[cur, n].scan(/(.*)(\n)?/) do
        if Regexp.last_match[2]
          @linenum += 1
          @charnum = 0
        else
          @charnum += Regexp.last_match[1].length
        end
      end
      self
    end
  end

  class LrTable
    attr_reader :heads, :answers

    def initialize
      @heads = {}
      @answers = LrAnswerTable.new
    end

    def clear
      @heads.clear
      @answers.clear
    end
  end

  class LrAnswerTable
    def initialize
      @answers = {}
    end

    def clear
      @answers.clear
    end

    def []=(index, rule_name, answer)
      entry = @answers[index] ||= {}
      entry[rule_name] = answer
    end

    def [](index, rule_name)
      @answers.dig(index, rule_name)
    end
  end

  class LrHead
    attr_accessor :rule_name, :invol, :eval

    def initialize
      @invol = {}
      @eval = {}
    end

    def invol_to_eval
      @eval.clear
      @invol.each do |k, v|
        @eval[k] = true
      end
    end
  end

  class ThunkChunk
    attr_accessor :thunks, :capts, :pos, :values
    attr_accessor :pos_loc

    def initialize
      super
      @thunks = []
      @capts = {}
      @pos = 0
      @values = {}
    end

    def resize_captures(len)
      len.times do |i|
        @capts[i] = Capture.new
      end
    end
  end

  class Thunk
  end

  class ThunkLeaf < Thunk
    attr_accessor :capt0, :capts, :value_refs, :action

    def initialize(action, capt0 = Capture.new, value_refs = {}, capts = {})
      @value_refs = value_refs
      @capts = capts
      @capt0 = capt0
      @action = action
    end

    def do_action(ctx, values, index)
      ctx.public_send(action, self, values, index)
    end
  end

  class ThunkNode < Thunk
    attr_accessor :thunks, :values, :index

    def initialize(thunks, values, index)
      @thunks = thunks
      @values = values
      @index = index
      values[index] ||= Value.new if values
    end

    def do_action(ctx, _values, _index)
      @thunks.each do |thunk|
        thunk.do_action(ctx, @values, @index)
      end
    end

    def clear
      @thunks.clear
    end
  end

  class LrEntry
    attr_accessor :rule, :head, :seed
  end

  class Capture
    attr_accessor :range_start, :range_end
    attr_accessor :start_loc, :end_loc

    def initialize(range_start = 0, range_end = 0, start_loc = nil, end_loc = nil)
      @range_start = range_start
      @range_end = range_end
      @start_loc = start_loc || Location.new
      @end_loc = end_loc || Location.new
    end

    def capture_string(buffer)
      @string ||= buffer[@range_start, @range_end - @range_start]
    end
  end

  class LrAnswer
    attr_accessor :lr, :chunk, :pos
    attr_accessor :pos_loc

    def initialize(lr, pos, pos_loc)
      @pos = pos
      @pos_loc = pos_loc
      @lr = lr
    end

    def chunk=(chunk)
      @lr = nil
      @chunk = chunk
    end
  end

  class Value
    attr_accessor :value
  end
end

class Packcr::Parser
  attr_accessor :rules, :rulehash

  def getc
    @ifile.getc
  end
end
